mapper.select:



        AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))

    and customer_id = #{customerId}
    and manager_id = #{managerId}
     and biz_contract.num  like concat('%', #{num}, '%')
     and biz_contract.customer_name like concat('%', #{customerName}, '%')
     and biz_contract.code like concat('%', #{code}, '%')
    and biz_contract.dianmian_name like concat('%', #{dianmianName}, '%')
     and biz_contract.customer_phone = #{customerPhone}
     and biz_contract.produce = #{produce}
     and biz_contract.operation = #{operation}
    and biz_contract.sign_date >= #{signDateStart}
    and biz_contract.sign_date <= #{signDateEnd}
    and biz_contract.begin_date >= #{beginDateStart}
    and biz_contract.begin_date <= #{beginDateEnd}
    and biz_contract.check_date >= #{checkDateStart}
    and biz_contract.check_date <= #{checkDateEnd}
    and biz_contract.end_date >= #{endDateStart}
    and biz_contract.end_date <= #{endDateEnd}
     and biz_contract.status between '0' and '1'
     and biz_contract.status = #{status}
     and biz_contract.type = #{type}
     and biz_contract.check_status <> '2'
     and biz_contract.check_status = #{checkStatus}
    and (
        instr(biz_contract.customer_name, #{keywords}) +
        instr(biz_contract.customer_phone, #{keywords}) +
        instr(biz_contract.num, #{keywords})
        ) > 0

    ${params.dataScope}

order by biz_contract.input_date desc

mapper.update:
update biz_contract
dept_id = #{deptId}, num = #{num}, code = #{code}, root_num = #{rootNum}, customer_name = #{customerName}, customer_id = #{customerId}, customer_phone = #{customerPhone}, type = #{type}, produce = #{produce}, dianmian_name = #{dianmianName}, dianmian_num = #{dianmianNum}, dianmian_province = #{dianmianProvince}, dianmian_city = #{dianmianCity}, dianmian_district = #{dianmianDistrict}, dianmian_address = #{dianmianAddress}, dianmian_longitude = #{dianmianLongitude}, dianmian_latitude = #{dianmianLatitude}, guarantee = #{guarantee}, fee = #{fee}, operation = #{operation}, manager_id = #{managerId}, manager = #{manager}, sign_date = #{signDate}, sign_user_id = #{signUserId}, sign_user = #{signUser}, check_status = #{checkStatus}, begin_date = #{beginDate}, end_date = #{endDate}, check_date = #{checkDate}, terminate_date = #{terminateDate}, status = #{status}, create_by = #{createBy}, remark = #{remark}, file = #{file}, terminate_file = #{terminateFile}, imgs = #{imgs},
where biz_contract.id = #{id}

update
    biz_contract bc
set
    bc.status = #{status}


        AND bc.num = #{num}

    and bc.begin_date <= date_format(now(),'%Y-%m-%d') and bc.end_date >= date_format(now(),'%Y-%m-%d') and bc.status = '1' -- 有效未执行
