select.where:

    AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))

and biz_customer.user_id = #{userId}
and biz_customer.name like concat('%', #{name}, '%')
and biz_customer.phone = #{phone}
and biz_customer.level = #{level}
and biz_customer.customer_needs = #{customerNeeds}
and biz_customer.company_name like concat('%', #{companyName},
    '%')

and biz_customer.province = #{province}
and biz_customer.dianmian_address = #{dianmianAddress}
and biz_customer.city = #{city}
and biz_customer.district = #{district}
and biz_customer.resource = #{resource}
and biz_customer.username like concat('%', #{username}, '%')
and date_format(biz_customer.input_date,'%y%m%d') >= date_format(#{inputDateStart},'%y%m%d')
and date_format(biz_customer.input_date,'%y%m%d')  <= date_format(#{inputDateEnd},'%y%m%d')
and biz_customer.status = #{status}

    and biz_customer.genjin_status = #{genjinStatus}

        and biz_customer.genjin_status not in ('5','7','8')


and biz_customer.genjin_date >= DATE_SUB(NOW(),INTERVAL #{daysSecond} DAY)
and biz_customer.genjin_date <= DATE_SUB(NOW(),INTERVAL #{daysFirst} DAY) 
and (
    instr(d.dept_name, #{keywords}) +
    instr(biz_customer.name, #{keywords}) +
    instr(biz_customer.phone, #{keywords})
    ) > 0

${params.dataScope}


    AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))

and biz_customer.user_id = #{userId}
and biz_customer.name like concat('%', #{name}, '%')
and biz_customer.phone = #{phone}
and biz_customer.level = #{level}
and biz_customer.customer_needs = #{customerNeeds}
and biz_customer.company_name like concat('%', #{companyName},
    '%')

and biz_customer.province = #{province}
and biz_customer.dianmian_address = #{dianmianAddress}
and biz_customer.city = #{city}
and biz_customer.district = #{district}
and biz_customer.resource = #{resource}
and biz_customer.username like concat('%', #{username}, '%')
and date_format(biz_customer.input_date,'%y%m%d') >= date_format(#{inputDateStart},'%y%m%d')
and date_format(biz_customer.input_date,'%y%m%d')  <= date_format(#{inputDateEnd},'%y%m%d')
and biz_customer.status = #{status}
and biz_customer.genjin_status = #{genjinStatus}
and biz_customer.genjin_date <= DATE_SUB(NOW(),INTERVAL #{daysFirst} DAY) 
and biz_customer.genjin_date >= DATE_SUB(NOW(),INTERVAL #{daysSecond} DAY)
and (
    instr(d.dept_name, #{keywords}) +
    instr(biz_customer.name, #{keywords}) +
    instr(biz_customer.phone, #{keywords})
    ) > 0

${params.dataScope}
