mapper.select:
select u.user_name, u.phonenumber, d.id, d.dept_name, cm.`name` company_name, p.post_name, IFNULL(xiansuo_num, 0) xiansuo_num, IFNULL(customer_num, 0) customer_num, IFNULL(gj_num, 0) gj_num, IFNULL(dandian_contract_num, 0) dandian_contract_num, IFNULL(quyu_contract_num, 0) quyu_contract_num, IFNULL(dandian_num, 0) dandian_num, IFNULL(quyu_dianmian_num, 0) quyu_dianmian_num, IFNULL(dandian_num,0) + IFNULL(quyu_dianmian_num,0) as dianmian_num from sys_user u left join sys_dept d ON u.dept_id = d.id left JOIN sys_company cm ON cm.id = d.company_id left JOIN sys_user_post up ON up.user_id = u.id left JOIN sys_post p ON p.post_id = up.post_id -- 线索和客户 left JOIN (SELECT c.user_id uid, count(case when c.`status` = 0 then 1 else null end) xiansuo_num, -- 线索状态为0 count(case when c.`status` = 1 then 1 else null end) customer_num -- 客户状态为1 FROM biz_customer c WHERE c.resource <= '6' and date_format(c.input_date,'%y%m%d') >= date_format(#{inputDateStart},'%y%m%d') and date_format(c.input_date,'%y%m%d') <= date_format(#{inputDateEnd},'%y%m%d') and DATE_FORMAT(c.input_date, '%Y%m' ) = DATE_FORMAT(CURDATE(), '%Y%m') GROUP BY uid ) as xiansuo_num on xiansuo_num.uid = u.id -- 客户跟进 LEFT JOIN (select gj.sys_user_id uid, COUNT(gj.id) as gj_num from biz_genjin gj where 1=1 and date_format(gj.genjin_date,'%y%m%d') >= date_format(#{inputDateStart},'%y%m%d') and date_format(gj.genjin_date,'%y%m%d') <= date_format(#{inputDateEnd},'%y%m%d') and DATE_FORMAT(gj.genjin_date, '%Y%m' ) = DATE_FORMAT(CURDATE(), '%Y%m') GROUP BY gj.sys_user_id ) as gj_num on gj_num.uid = u.id -- 合同 LEFT JOIN ( SELECT contract.manager_id uid, count(case when contract.produce = 0 then 1 else null end) dandian_contract_num, count(case when contract.produce = 1 then 1 else null end) quyu_contract_num FROM biz_contract contract where 1=1 and date_format(contract.sign_date,'%y%m%d') >= date_format(#{inputDateStart},'%y%m%d') and date_format(contract.sign_date,'%y%m%d') <= date_format(#{inputDateEnd},'%y%m%d') and DATE_FORMAT(contract.sign_date, '%Y%m' ) = DATE_FORMAT(CURDATE(), '%Y%m') GROUP BY contract.manager_id ) contract_num ON contract_num.uid = u.id -- 店面 LEFT JOIN ( SELECT dianmian.sys_user_id uid, count(case when dianmian.type = 0 then 1 else null end) dandian_num, -- 单店 count(case when dianmian.type = 1 then 1 else null end) quyu_dianmian_num -- 区域 FROM biz_dianmian dianmian WHERE dianmian.status BETWEEN 1 and 3 -- 闭店状态为0 需要排除出去，这里使用BETWEEN 1 and 3 and date_format(dianmian.set_date,'%y%m%d') >= date_format(#{inputDateStart},'%y%m%d') and date_format(dianmian.set_date,'%y%m%d') <= date_format(#{inputDateEnd},'%y%m%d') and DATE_FORMAT(dianmian.set_date, '%Y%m' ) = DATE_FORMAT(CURDATE(), '%Y%m') GROUP BY dianmian.sys_user_id ) dianmian ON dianmian.uid = u.id
AND (u.dept_id = #{deptId} OR u.dept_id IN ( SELECT t.id FROM sys_dept t WHERE find_in_set(#{deptId}, ancestors) ))
and u.user_name like concat('%', #{username}, '%') ${params.dataScope}
