mapper.select:
select u.user_name, u.phonenumber, d.id, d.dept_name, cm.`name` company_name, p.post_name, lvyue_fee, jingying_manager_fee, yunying_manager_fee, system_use_fee, system_maintenance_fee, daiban_fee, guohuo_fee, lvyue_fee + jingying_manager_fee + yunying_manager_fee + system_use_fee + system_maintenance_fee + daiban_fee + guohuo_fee as total_fee from sys_user u left join sys_dept d ON u.dept_id = d.id left JOIN sys_company cm ON cm.id = u.company_id left JOIN sys_user_post up ON up.user_id = u.id left JOIN sys_post p ON p.post_id = up.post_id left JOIN ( select fee.receiver_id uid, SUM(case when fee.type = '履约保证金' then fee.amount else 0 end) lvyue_fee, SUM(case when fee.type = '经营管理费' then fee.amount else 0 end) jingying_manager_fee, SUM(case when fee.type = '运营管理费' then fee.amount else 0 end) yunying_manager_fee, SUM(case when fee.type = '系统使用费' then fee.amount else 0 end) system_use_fee, SUM(case when fee.type = '系统维护费' then fee.amount else 0 end) system_maintenance_fee, SUM(case when fee.type = '代办费' then fee.amount else 0 end) daiban_fee, SUM(case when fee.type = '过户费' then fee.amount else 0 end) guohuo_fee FROM biz_contract_fee fee
and fee.shoukuan_date > #{inputDate}
GROUP BY fee.receiver_id ) fee ON fee.uid = u.id -- 查询条件
and d.id = #{deptId} and u.id = #{userId}
