mapper.select:
SELECT ( SELECT count(1) FROM biz_dianmian dm WHERE dm.`status` = 1 and DATE_FORMAT( dm.input_date, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' ) and YEAR(dm.input_date) = YEAR(NOW()) ) as yingye_dianmian, ( SELECT count(1) FROM biz_dianmian dm
and DATE_FORMAT( dm.input_date, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' ) and YEAR(dm.input_date) = YEAR(NOW())
) as all_dianmian, ( SELECT COUNT(1) FROM biz_contract bc WHERE bc.type = '0' -- 新签 and DATE_FORMAT( bc.sign_date, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' ) and YEAR(bc.sign_date) = YEAR(NOW()) ) as new_contract, ( SELECT COUNT(1) FROM biz_contract bc WHERE bc.type = '1' -- 续签 and DATE_FORMAT( bc.sign_date, '%Y%m' ) = DATE_FORMAT( CURDATE() , '%Y%m' ) and YEAR(bc.sign_date) = YEAR(NOW()) ) as renew_contract
